delete from tap_schema.key_columns11 where
key_id in (select key_id from tap_schema.keys11 where 
    from_table in   (select table_name from tap_schema.tables11 where lower(table_name) like 'tapdata.%')
    or
    target_table in (select table_name from tap_schema.tables11 where lower(table_name) like 'tapdata.%')
)
;

-- delete keys from tables in the tap_schema schema
delete from tap_schema.keys11 where 
from_table in   (select table_name from tap_schema.tables11 where lower(table_name) like 'tapdata.%')
or
target_table in (select table_name from tap_schema.tables11 where lower(table_name) like 'tapdata.%')
;

-- delete columns from tables in the tap_schema schema
delete from tap_schema.columns11 where table_name in 
(select table_name from tap_schema.tables11 where lower(table_name) like 'tapdata.%')
;

-- delete tables
delete from tap_schema.tables11 where lower(table_name) like 'tapdata.%'
;

-- delete schema
delete from tap_schema.schemas11 where lower(schema_name) = 'tapdata'
;





